package com.ravi.learn.sortAlgo;

public class SampleForJ {

	 int x = 30;

	public void update() {
		int x=15;
		x++;
		System.out.println(x);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		SampleForJ s = new SampleForJ();
		System.out.println(s.x);
		s.update();
		System.out.println(s.x);

	}

}
